<?php class glue {static function stick ($a) {$b = strtoupper($_SERVER['REQUEST_METHOD']);$c = str_replace(str_replace(array('/index.php', ' '), array('', '%20'), $_SERVER['SCRIPT_NAME']), '', $_SERVER['REQUEST_URI']);$d = false;krsort($a);foreach ($a as $e => $f) {$e = str_replace('/', '\/', $e);$e = '^' . $e . '\/?$';if (preg_match("/$e/i", $c, $g)) {$d = true;if (class_exists($f)) {$h = new $f;if (method_exists($h, $b)) {$g = isset($g[1]) ? explode("/", preg_replace("/\/$/", "", $g[1])) : array();call_user_func_array(array($h, $b), $g);} else {throw new BadMethodCallException("Method, $b, not supported.");}} else {throw new Exception("Class, $f, not found.");} break;}} if (!$d) {throw new Exception("URL, $c, not found.");}}}